mapper.select:
select * from BUCKETLIST
where FAMILYSEQ = #{familySeq}
and TYPE = 'Family'

mapper.update:
update
BUCKETLIST set VISIBLE = 'n'
WHERE BUCKETSEQ = #{bucketSeq}

mapper.select:
select * from BUCKETLIST
where USERID = #{userId}
and TYPE = 'Personal'

mapper.update:
update
BUCKETLIST set FINISHDATE = SYSDATE
WHERE BUCKETSEQ = #{bucketSeq}

mapper.select:

SELECT COUNT(*)
FROM BUCKETREPLY
WHERE bucketSeq = #{bucketSeq}
