mapper.update:
update
BUCKETLIST set VISIBLE = 'n'
WHERE BUCKETSEQ = #{bucketSeq}

mapper.select:
select * from BUCKETLIST
where USERID = #{userId}
and TYPE = 'Personal'

mapper.update:
update BUCKETLIST set 
	title = #{title},
	contents = #{contents},
	fileData = #{fileData},
	fileName = #{fileName}

	where bucketSeq = #{bucketSeq}

update BUCKETLIST set 
	title = #{title},
	contents = #{contents}

	where bucketSeq = #{bucketSeq}

update
BUCKETLIST set FINISHDATE = SYSDATE
WHERE BUCKETSEQ = #{bucketSeq}

mapper.select:

SELECT COUNT(*)
FROM BUCKETREPLY
WHERE bucketSeq = #{bucketSeq}

mapper.update:

update BUCKETREPLY set 
    replyContents = #{replyContents, jdbcType=CLOB}
    WHERE bucketReplySeq = #{bucketReplySeq}
