mapper.select:

SELECT familySeq
FROM itdauser
WHERE userId=#{userId}


SELECT COUNT(*)
FROM itdauser
WHERE familySeq = #{familySeq}


SELECT userId
FROM itdauser
WHERE familySeq = #{familySeq}

mapper.update:

UPDATE NICKNAME SET 
targetUserId = #{targetUserID},
targetNickName = #{targetNickName}
WHERE userId = #{userId}
