mapper.select:

SELECT familySeq
FROM itdauser
WHERE userId=#{userId}


SELECT COUNT(*)
FROM itdauser
WHERE familySeq = #{familySeq}


SELECT userId
FROM itdauser
WHERE familySeq = #{familySeq}

mapper.update:

UPDATE ITDAUSER SET (userPw, userAddress, userAddressDetail, userPhone, email)
= (SELECT #{userPw}, #{userAddress}, #{userAddressDetail}, #{userPhone}, #{email} FROM DUAL)
WHERE userId = #{userId}
